require("./object_methods")

const utility = require("./utility")

const drop = function(number = 1) {
	return this.slice(number);
}
const dropRight = function(number = 1) {
	return this.slice(0, this.length - number)
}

const arrayMethods = {}
arrayMethods.define("drop", drop)
arrayMethods.define("dropRight", dropRight)

utility.setMethodPairs(arrayMethods, Array.prototype)
Object.freeze(Array.prototype)
