const objectMethods = require("./object_methods")
const utility = require("./utility")

const iterate = function* () {
	let value = this.valueOf()
	let counter = 1
	while(counter <= value) {
		yield counter
		counter += 1
	}
}

const numberMethods = {}

numberMethods.define(Symbol.iterator, iterate)

utility.setMethodPairs(numberMethods, Number.prototype)
Object.freeze(Number.prototype)
